import { useState } from 'react';
import BookList from './components/BookList.js';
import Categories from './components/Categories';

import axios from 'axios';
import useAsync from './hooks/useAsync';
import './App.css';


async function getBooks(category) {
  // Got it working by throwing my tpl_json.json file into the Public folder and calling it by:
  const response = await axios.get('tpl_json.json');
  const filteredData = response.data.filter((book) => book.category === category);
  return filteredData
  // return response.data;
}

function App() {
  const [category, setCategory] = useState('')
  const [state] = useAsync(() => getBooks(category), [],);
  const { loading, data, error } = state;

  return (
    <div>
      <header>
        <h1>Hello</h1>
      </header>
      <main>
        <Categories onCategory={setCategory} />

        <BookList
          loading={loading}
          data={data}
          error={error}
        />
      </main>
      <footer>

      </footer>
    </div>
  )
};

export default App;
