import { useState } from 'react';
import BookList from './components/BookList.js';
import Categories from './components/Categories';

import axios from 'axios';
import useAsync from './hooks/useAsync';
import './App.css';


async function getBooks(category) {
  // Got it working by throwing my tpl_json.json file into the Public folder and calling it by:
  const response = await axios.get('tpl_json.json');
  if (category === '') {
    console.log(category)
    console.log(response.data.books[0])
    return response.data.books[0];
  } else {

    const filteredData = await response.data.books[0].filter((book) => book.category === category);
    console.log(filteredData);
    return filteredData
  }
  // return response.data;
}

function App() {
  const [category, setCategory] = useState('')
  const [state] = useAsync(() => getBooks(category), [category],);
  const { loading, data, error } = state;
  console.log(category)
  return (
    <div>
      <header>
        <h1>Hello</h1>
      </header>
      <main>
        <Categories onCategory={setCategory} />

        <BookList
          loading={loading}
          data={data}
          error={error}
        />
      </main>
      <footer>

      </footer>
    </div>
  )
};

export default App;
